import React from 'react';
import {useState, useEffect} from 'react';
import axios from 'axios';





 const carCard = (props) => {


    const [cars, setCars] = useState([]);
  const [newMake, setNewMake] = useState('');
  const [newModel, setNewModel] = useState(false);
  const [newImage, setNewImage] = useState('');
  const [newColor, setNewColor] = useState('');
  const [newPrice, setNewPrice] = useState(0);
  const [newYear, setNewYear] = useState(0);

const handleDelete = (carData)=>{


    axios
        .delete(`http://localhost:3000/cars/${carData._id}`)
        .then(()=>{
            axios
                .get('http://localhost:3000/cars')
                .then((response)=>{
                  setCars(response.data)
                })
        })
  }

  const handleToggleComplete = (carData)=>{
    axios
        .put(
            `http://localhost:3000/animals/${carData._id}`,
            {
              make:newMake,
              model: newModel,
              year: newYear,
              color: newColor,
              image: newImage,
              price: newPrice,
            }
        )
        .then(()=>{
            axios
                .get('http://localhost:3000/animals')
                .then((response)=>{
                    setCars(response.data)
                })
        })
  }
     return(
     <section>
        <h2>Cars</h2>
        <ul>
        {
            props.cars.map((car) => {

                return <div class="aniCard" key={car._id}>
                Make: {car.make}<br/>
                Model: {car.model}<br/>
                Year: {car.make}<br/>
                Color: {car.model}<br/>
                <img src={car.image}/><br/>
                Price: ${car.price}<br/>

                 <button onClick={ ()=>{ handleToggleComplete(car) } }>edit</button>
                <button onClick={ ()=> { handleDelete(car) } }>Delete</button>
                </div>
            })
        }

        </ul>
    </section>

    );

 }



export default carCard;
