const handleNewName = (event)=>{

  setNewCar(event.target.value);
}
const handleNewComplete = (event)=>{
  setNewComplete(event.target.checked);
}
const handleNewImage = (event) => {
  setNewImage(event.target.value)
}
const handleNewSpecies = (event) => {
  setNewModel(event.target.value)
}

const handleNewTodoFormSubmit = (event)=>{
  event.preventDefault();

  axios.post(
      'http://localhost:3000/animals',
      {
          name:newName,
          species: newSpecies,
          image: newImage,
          reservedForAdoption: newComplete,
      }
  ).then(()=>{
      axios
          .get('http://localhost:3000/animals')
          .then((response)=>{
              setAnimals(response.data)
          })
      })
}

const handleDelete = (animalData)=>{
  axios
      .delete(`http://localhost:3000/animals/${animalData._id}`)
      .then(()=>{
          axios
              .get('http://localhost:3000/animals')
              .then((response)=>{
                setAnimals(response.data)
              })
      })
}

const handleToggleComplete = (animal)=>{
  axios
      .put(
          `http://localhost:3000/animals/${animal._id}`,
          {
            name:newName,
            species: newSpecies,
            image: newImage,
            reservedForAdoption: newComplete,
          }
      )
      .then(()=>{
          axios
              .get('http://localhost:3000/animals')
              .then((response)=>{
                  setAnimals(response.data)
              })
      })
}

