                <form onSubmit={handleNewTodoFormSubmit}>
                  Name: <input type="text" onChange={handleNewName}/><br/>
                  Species: <input type="text" onChange={handleNewSpecies}/><br/>
                  Image: <input type="text" onChange={handleNewImage}/><br/>
                  Reserved: <input type="checkbox" onChange={handleNewComplete}/><br/>
                    <input type="submit" value="Create new Animal"/>
                </form>

            </section>
            <section>
    <h2>Animals</h2>
    <ul>
    {
      animals.map((animal) => {

            return <div class="aniCard" key={animal._id}>
            Name: {animal.name}<br/>
            Species: {animal.species}<br/>
            <img src={animal.image}/><br/>
            Reserved: {animal.reservedForAdoption?'Reserved': 'Able to Reserve'}<br/>
             <button onClick={ ()=>{ handleToggleComplete(animal) } }>edit</button>
            <button onClick={ ()=> { handleDelete(animal) } }>Delete</button>
            </div>
        })
    }

    </ul>
</section>
        </main>
